import React from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import IconButton from "@material-ui/core/IconButton";
import Input from "@material-ui/core/Input";
import FilledInput from "@material-ui/core/FilledInput";
import OutlinedInput from "@material-ui/core/OutlinedInput";
import InputLabel from "@material-ui/core/InputLabel";
import InputAdornment from "@material-ui/core/InputAdornment";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import TextField from "@material-ui/core/TextField";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import TextareaAutosize from "@material-ui/core/TextareaAutosize";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexWrap: "wrap",
  },
  margin: {
    margin: theme.spacing(1),
  },
  withoutLabel: {
    marginTop: theme.spacing(3),
  },
  textField: {
    width: "25ch",
  },
}));

export default function Form() {
  const classes = useStyles();
  const [values, setValues] = React.useState({
    amount: "",
    password: "",
    weight: "",
    weightRange: "",
    showPassword: false,
  });

  const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
  };

  const handleClickShowPassword = () => {
    setValues({ ...values, showPassword: !values.showPassword });
  };

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  return (
    <div className={classes.root}>
      <div>
        <h2>Edit Profile</h2>
        <form>
          <FormControl
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="firstname"
              label="firstname"
              type="string"
              variant="outlined"
              size="small"
            />
          </FormControl>

          <FormControl
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="lastname"
              label="lastname"
              type="string"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
            />
          </FormControl>

          <FormControl
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="email"
              label="email"
              type="email"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
            />
          </FormControl>
          <FormControl
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="username"
              label="username"
              type="string"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
            />
          </FormControl>
          <FormControl
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="date of birth"
              label="date of birth"
              type="date"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
            />
          </FormControl>
          <FormControl
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="gender"
              label="gender"
              type="string"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
            />
          </FormControl>

          <FormControl
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="bio"
              label="bio"
              type="textarea"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
            />
          </FormControl>

          <FormControl
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="interest"
              label="interest"
              type="search"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
            />
          </FormControl>

          <FormControl
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="Phone Number"
              label="Phone Number"
              type="number"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
            />
          </FormControl>

          <FormControl
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="Website"
              label="Website"
              type="search"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
            />
          </FormControl>

          <FormControl
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextareaAutosize
              aria-label="minimum height"
              rowsMin={3}
              placeholder="Minimum 3 rows"
            />
          </FormControl>
        </form>
      </div>
    </div>
  );
}
