import React, { useState, useEffect } from "react";
//
import axios from "axios";

//cookes manager
import cookie from "js-cookie";

// style pakeges
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";

// import a image file |tempory|
import desktopPhoto from "./aa.jpg";

// style component
const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});

//main
export default function Face() {
  // class object
  const classes = useStyles();

  const [faceInfo, setFaceInfo] = useState({
    firstname: "",
    lastname: "",
    email: "",
    username: "",
    dateofbirth: "",
    gender: "",
    bio: "",
    interest: "",
  });

  useEffect(() => {
    console.log(" |54 Debug | axios >> ");
    console.log(
      "|56 Debug | " +
        cookie.get("auth-token") +
        "\n user _ID >>> " +
        cookie.get("_id")
    );
    axios({
      // `method` is the request method to be used when making the request
      method: "post",

      // `url` is the server URL that will be used for the request
      url: "api/profile/face",

      // `baseURL` will be prepended to `url` unless `url` is absolute.
      baseURL: "http://localhost:5000",

      // `headers` are custom headers to be sent
      headers: { Authorization: "Bearer" + " " + cookie.get("auth-token") },

      // `data` is the data to be sent as the request body
      data: {
        _id: cookie.get("_id"),
      },
    })
      .then((result) => {



        console.log("|80 Debug | result >> " + JSON.stringify(result.data.firstname));
        setFaceInfo((prev) => {
          prev.firstname = result.data.firstname;
          prev.lastname = result.data.lastname;
          prev.email = result.data.email;
          // prev.username = result.data.username;
          // prev.dateofbirth = result.data.dateofbirth;
          // prev.gender = result.data.gender;
          // prev.bio = result.data.bio;
          // prev.interest = result.data.interest;
        });
        console.log("|84 Debug | faceInfo >> " + JSON.stringify(faceInfo));
      })
      .catch((err) => {
        console.log("|83 Debug | err >> " + err);
      });
  }, []);

  // Rendering Part
  return (
    <div>
      <Card className={classes.root}>
        <img
          src={desktopPhoto}
          style={{
            float: "right",
            borderRadius: "50%",
            width: "25%",
            padding: "10px",
          }}
        />
        <CardContent>
          <Typography variant="h5" component="h2">
            Rupam Bumba
          </Typography>
          <Typography className={classes.pos} color="textSecondary">
            @rupambumba
          </Typography>
          <Typography variant="body2" component="p">
            well meaning and kindly.
            <br />
            {'"a benevolent smile"'}
          </Typography>
        </CardContent>

        <CardActions>
          <Button size="small">follower 123</Button>
          <Button size="small">folowing 321</Button>
          <Button size="small">freands 231</Button>
        </CardActions>
      </Card>
    </div>
  );
}
