import React from "react";

// style pakeges
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";

import desktopPhoto from "./aa.jpeg"

// style component
const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});

//main
export default function Face() {
  const classes = useStyles();
  // const bull = <span className={classes.bullet}>•</span>;

  return (
    <div>
      <Card className={classes.root}>
         <img src={desktopPhoto} style={{float:"right" , borderRadius: '1%', width: '1%' } } />
        <CardContent>
          <Typography variant="h5" component="h2">
            Rupam Bumba
          </Typography>
          <Typography className={classes.pos} color="textSecondary">
            @rupambumba
          </Typography>
          <Typography variant="body2" component="p">
            well meaning and kindly.
            <br />
            {'"a benevolent smile"'}
          </Typography>

        </CardContent>

        <CardActions>
          <Button size="small">follower 123</Button>
          <Button size="small">folowing 321</Button>
          <Button size="small">freands 231</Button>
        </CardActions>

      </Card>

    </div>
  );
}
