import React, { useEffect } from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import IconButton from "@material-ui/core/IconButton";
import Button from "@material-ui/core/Button";
import Input from "@material-ui/core/Input";
import FilledInput from "@material-ui/core/FilledInput";
import OutlinedInput from "@material-ui/core/OutlinedInput";
import InputLabel from "@material-ui/core/InputLabel";
import InputAdornment from "@material-ui/core/InputAdornment";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import TextField from "@material-ui/core/TextField";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import axios from "axios";

const useStyles = makeStyles(theme => ({
  root: {
    display: "flex",
    flexWrap: "wrap"
  },
  margin: {
    margin: theme.spacing(1)
  },
  withoutLabel: {
    marginTop: theme.spacing(3)
  },
  textField: {
    width: 200
  }
}));






export default function Login() {
  const classes = useStyles();
  use
  const [values, setValues] = React.useState({
    email: "",
    password: "",
    showPassword: false
  });

  const handleChange = prop => event => {
    setValues({ ...values, [prop]: event.target.value });
    console.log(JSON.stringify(values));

  };

  const handleClickShowPassword = () => {
    setValues({ ...values, showPassword: !values.showPassword });

  };

  const handleMouseDownPassword = event => {
    event.preventDefault();
  };

  const handleFormSubmit = evnt => {
    evnt.preventDefault()
    console.log("login effect subnit form call >> ");

  };



  useEffect(() => {
    axios
    .post('http://localhost:5000/api/user/login',{
      "email": values.email,
      "password": values.password
    })
    .then(result =>{
      console.log("axios >> user >> login >> ");
      console.log(result);
    })
    .catch(err =>{
      console.log("axios >> user >> error >> ");
      console.log(err);
    })
  }, )

  return (
    <div className={classes.root}>
      <h2>Log in</h2>
      <div>
        <form onSubmit = {handleFormSubmit}>
          <FormControl // email
            fullWidth
            className={
              (clsx(classes.margin, classes.textField), classes.margin)
            }
            variant="outlined"
          >
            <TextField
              id="outlined-search"
              label="Email"
              type="search"
              variant="outlined"
              onChange={handleChange("email")}
            />
          </FormControl>

          <FormControl // Password
            fullWidth
            className={
              (clsx(classes.margin, classes.textField), classes.margin)
            }
            variant="outlined"
          >
            <InputLabel htmlFor="outlined-adornment-password">
              Password
            </InputLabel>
            <OutlinedInput
              id="outlined-adornment-password"
              type={values.showPassword ? "text" : "password"}
              value={values.password}
              onChange={handleChange("password")}
              endAdornment={
                <InputAdornment position="end">
                  <IconButton
                    aria-label="toggle password visibility"
                    onClick={handleClickShowPassword}
                    onMouseDown={handleMouseDownPassword}
                    edge="end"
                  >
                    {values.showPassword ? <Visibility /> : <VisibilityOff />}
                  </IconButton>
                </InputAdornment>
              }
              labelWidth={70}
            />
          </FormControl>

          <FormControl // Login  Button
            className={classes.margin}
            variant="outlined"
          >
            <Button
              variant="contained"
              color="primary"
              size="large"
              type="submit"
            >
              LogIn
            </Button>
          </FormControl>

        </form>
      </div>
    </div>
  );
}
