import React ,{useState,useEffect} from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import IconButton from "@material-ui/core/IconButton";
import Input from "@material-ui/core/Input";
import FilledInput from "@material-ui/core/FilledInput";
import OutlinedInput from "@material-ui/core/OutlinedInput";
import InputLabel from "@material-ui/core/InputLabel";
import InputAdornment from "@material-ui/core/InputAdornment";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import TextField from "@material-ui/core/TextField";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import TextareaAutosize from "@material-ui/core/TextareaAutosize";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexWrap: "wrap",
  },
  margin: {
    margin: theme.spacing(1),
  },
  withoutLabel: {
    marginTop: theme.spacing(3),
  },
  textField: {
    width: "25ch",
  },
}));

//main
export default function Form() {
  const classes = useStyles();
  const [values, setValues] = useState({
    firstname: "",
    lastname: "",
    email: "",
    username: "",
    dateofbirth: "",
    gender: "",
    phonenumber :"",
    website : "",
    bio: "",
    interest: "",
  });

  // effect hook for fatch data of users
  useEffect(() => {
    console.log(" |54 Debug | axios >> ");
    console.log(
      "|56 Debug | " +
        cookie.get("auth-token") +
        "\n user _ID >>> " +
        cookie.get("_id")
    );
    axios({
      // `method` is the request method to be used when making the request
      method: "post",

      // `url` is the server URL that will be used for the request
      url: "api/profile/face",

      // `baseURL` will be prepended to `url` unless `url` is absolute.
      baseURL: "http://localhost:5000",

      // `headers` are custom headers to be sent
      headers: { Authorization: "Bearer" + " " + cookie.get("auth-token") },

      // `data` is the data to be sent as the request body
      data: {
        _id: cookie.get("_id"),
      },
    })
      .then((result) => {
        console.log(
          "|84 Debug | JSON.stringify(result) >> " + JSON.stringify(result)
        );
        console.log("|86 Debug | result.data[0]._id >> " + result.data[0]._id);

        setFaceInfo(() => ({
          firstname: result.data[0].firstname,
          lastname: result.data[0].lastname,
          email: result.data[0].email,
          username: result.data[0].username,
          phonenumber :result.data[0].phonenumber,
          website:result.data[0].website,
          dateofbirth: result.data[0].dateofbirth,
          gender: result.data[0].gender,
          bio: result.data[0].bio,
          interest: result.data[0].interest,
          subscribers: result.data[0].subscribers,
          subscribe: result.data[0].subscribe,
          friends: result.data[0].friends,
        }));
        console.log(
          "|102 Debug | JSON.stringify(faceInfo) >> " + JSON.stringify(faceInfo)
        );
        console.log(
          "|140 Debug |  faceInfo.firstname >> " + faceInfo.firstname
        );
      })
      .catch((err) => {
        console.log("|107 Debug | err >> " + err);
      });
  }, []);

  const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
  };

  const handleClickShowPassword = () => {
    setValues({ ...values, showPassword: !values.showPassword });
  };

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };






  return (
    <div className={classes.root}>
      <div>
        <h2>Edit Profile</h2>
        <form>

          <FormControl //firstname
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="firstname"
              label="firstname"
              type="string"
              variant="outlined"
              size="small"
              value ="valus"
            />
          </FormControl>

          <FormControl //lastname
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="lastname"
              label="lastname"
              type="string"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
              value ="valus"
            />
          </FormControl>


          <FormControl
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="username"
              label="username"
              type="string"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}


            />
          </FormControl>

          <FormControl //email
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="email"
              label="email"
              type="email"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
            />
          </FormControl>

          <FormControl //Phone Number
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField 
              id="Phone Number"
              label="Phone Number"
              type="number"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
            />
          </FormControl>






          <FormControl //bio
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="bio"
              label="bio"
              type="textarea"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
              multiline
              rows={4}
            />
          </FormControl>

          <FormControl //interest
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="interest"
              label="interest"
              type="search"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
            />
          </FormControl>



          <FormControl //Website
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="Website"
              label="Website"
              type="search"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
            />
          </FormControl>

          <FormControl //date of birth
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="date of birth"
              label="date of birth"
              type="date"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
              value="2115-08-09"

            />
          </FormControl>

          <FormControl // gender
            className={clsx(classes.margin, classes.textField)}
            variant="outlined"
            style={{ width: "100%" }}
          >
            <TextField
              id="gender"
              label="gender"
              type="string"
              variant="outlined"
              size="small"
              style={{ width: "100%" }}
            />
          </FormControl>


        </form>
      </div>
    </div>
  );
}
