import React from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import IconButton from "@material-ui/core/IconButton";
import Input from "@material-ui/core/Input";
import FilledInput from "@material-ui/core/FilledInput";
import OutlinedInput from "@material-ui/core/OutlinedInput";
import InputLabel from "@material-ui/core/InputLabel";
import InputAdornment from "@material-ui/core/InputAdornment";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import TextField from "@material-ui/core/TextField";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";

const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: "100%",
  },
  margin: {
    margin: theme.spacing(1),
  },
  withoutLabel: {
    marginTop: theme.spacing(3),
  },
  textField: {},
  input: {
    display: "none", // not show HTML input  icon
  },
}));

export default function InputAdornments() {
  const classes = useStyles();
  const [values, setValues] = React.useState({
    postImageFileName: "",
    enctype: "multipart/form-data",
    _user_id: "",
    text: "",
    link: "",
    file: "",
  });

  const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
    console.log(" | 51 Debug values| "+ JSON.stringify( values ));

  };

  const handleClickShowPassword = () => {
    setValues({ ...values, showPassword: !values.showPassword });
  };

  const handelsubmit = (event) => {
    event.preventDefault();
  };

  return (
    <div className={classes.root}>
      <div>
        <form onSubmit={handelsubmit}>
          <Card className={classes.root}>
            <FormControl
              className={clsx(classes.margin, classes.textField)}
              variant="outlined"
              style={{ width: "97%" }}
            >
              <TextField
                id="text"
                placeholder="Wellcome To Memebar"
                type="textarea"
                multiline
                fullWidth
                rows={2}
                variant="outlined"
                style={{ width: "100%" }}
                onChange={handleChange("file")}
              />
            </FormControl>

            <div>
              <Grid container spacing={0}>
                <Grid item xs={6} style={{ background: "" }}>
                  <FormControl
                    className={clsx(classes.margin, classes.textField)}
                    variant="outlined"
                  >
                    <input
                      id="contained-button-file"
                      accept="image/*"
                      className={classes.input}
                      //multiple
                      type="file"
                onChange={handleChange("text")}

                    />
                    <label htmlFor="contained-button-file">
                      <Button
                        variant="contained"
                        color="primary"
                        component="span"
                      >
                        Upload
                      </Button>
                      <span> Choose a File </span>
                    </label>
                  </FormControl>
                </Grid>

                <Grid item xs={3} style={{ background: "" }}></Grid>
                <Grid item xs={3} style={{ background: "" }}>
                  <FormControl
                    className={clsx(classes.margin, classes.textField)}
                    variant="outlined"
                    style={{ width: "85%" }}
                  >
                    <Button variant="contained" color="primary" type="submit">
                      post
                    </Button>
                  </FormControl>
                </Grid>
              </Grid>
            </div>
          </Card>
        </form>
      </div>
    </div>
  );
}
