import React, { useState, useEffect } from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import IconButton from "@material-ui/core/IconButton";
import Button from "@material-ui/core/Button";
import Input from "@material-ui/core/Input";
import FilledInput from "@material-ui/core/FilledInput";
import OutlinedInput from "@material-ui/core/OutlinedInput";
import InputLabel from "@material-ui/core/InputLabel";
import InputAdornment from "@material-ui/core/InputAdornment";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import TextField from "@material-ui/core/TextField";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import axios from "axios";

const useStyles = makeStyles(theme => ({
  root: {
    display: "flex",
    flexWrap: "wrap"
  },
  margin: {
    margin: theme.spacing(1)
  },
  withoutLabel: {
    marginTop: theme.spacing(3)
  },
  textField: {
    width: 400
  }
}));

export default function Singup() {

  const classes = useStyles();
const [Singuptrigger, setSinguptrigger] = useState(false)
  const [values, setValues] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    showPassword: false
  });

  const handleChange = prop => event => {
    // useState Handeling
    setValues({ ...values, [prop]: event.target.value });
    console.log("====>>>>" + JSON.stringify(values));
  };

  const handleClickShowPassword = () => {
    // password toggler
    setValues({ ...values, showPassword: !values.showPassword });
  };

  const handleMouseDownPassword = event => {
    // password eye
    event.preventDefault();
  };

  const handleFromSubmit = evnt => {
    evnt.preventDefault();
    console.log('handleFromSubmit');
    setSinguptrigger(!Singuptrigger)
    console.log(Singuptrigger);


  }; 

  useEffect(() => {
    console.log('UseEffect======+++>>>');
    axios
      .post("localhost:5000/api/user/singup", {
        firstname: values.firstname,
        lastname: values.lastname,
        email: values.email,
        password: values.password
      })
      .then(result =>{
        console.log("axios >> result :" + result );

      } )
      .catch(err =>{
        console.log('axios Error '+err );
      });
  }, [Singuptrigger]);

  return (
    <div className={classes.root}>
      <h2>Sing up</h2>
      <div>
        <form onSubmit={handleFromSubmit}>
          <FormControl //First name
            fullWidth
            className={
              (clsx(classes.margin, classes.textField), classes.margin)
            }
            variant="outlined"
          >
            <TextField
              id="outlined-search"
              label="First name"
              type="search"
              variant="outlined"
              onChange={handleChange("firstname")}
            />
          </FormControl>

          <FormControl //Last name
            fullWidth
            className={
              (clsx(classes.margin, classes.textField), classes.margin)
            }
            variant="outlined"
          >
            <TextField
              id="outlined-search"
              label="Last name"
              type="search"
              variant="outlined"
              onChange={handleChange("lastname")}
            />
          </FormControl>

          <FormControl //email"
            fullWidth
            className={
              (clsx(classes.margin, classes.textField), classes.margin)
            }
            variant="outlined"
          >
            <TextField
              id="outlined-search"
              label="email"
              type="email"
              variant="outlined"
              onChange={handleChange("email")}
            />
          </FormControl>

          <FormControl // password
            fullWidth
            className={
              (clsx(classes.margin, classes.textField), classes.margin)
            }
            variant="outlined"
          >
            <InputLabel htmlFor="outlined-adornment-password">
              Password
            </InputLabel>
            <OutlinedInput
              id="outlined-adornment-password"
              type={values.showPassword ? "text" : "password"}
              value={values.password}
              onChange={handleChange("password")}
              endAdornment={
                <InputAdornment position="end">
                  <IconButton
                    aria-label="toggle password visibility"
                    onClick={handleClickShowPassword}
                    onMouseDown={handleMouseDownPassword}
                    edge="end"
                  >
                    {values.showPassword ? <Visibility /> : <VisibilityOff />}
                  </IconButton>
                </InputAdornment>
              }
              labelWidth={70}
            />
          </FormControl>

          <FormControl //email"

            className={
              ( classes.margin)
            }
            variant="outlined"
          >
            <Button variant="contained" color="primary" size="large" type="submit">
              SingUp
            </Button>
          </FormControl>
        </form>
      </div>
    </div>
  );
}
